import { useLocation } from "react-router";

/* 액션 타입 선언 */
const SUMMARY = 'profile/SUMMARY';
const PROJECTS = 'profile/PROJECTS';
const STATISTICS = 'profile/STATISTICS';

/* 액션 생성함수 선언 */
export const summary = () => ({ type: SUMMARY });
export const projects = () => ({ type: PROJECTS });
export const statistics = () => ({ type: STATISTICS });

/* 초기 상태 선언 */
const initialState = {
    id: '',
    page: 'summary'
};

/* 리듀서 선언 */
export default function profile(state = initialState, action) {
    const location = useLocation();
    user = location.pathname.split('/')[1];
    console.log(user);
    switch (action.type) {
        case SUMMARY:
            return ({
                ...state,
                id: user,
                page: 'summary'
            });
        case PROJECTS:
            return ({
                ...state,
                id: user,
                page: 'projects'
            });
        case STATISTICS:
            return ({
                ...state,
                id: user,
                page: 'statistics'
            });
        default:
            return state;
    }
}
