/* 액션 타입 선언 */
const USER_SUMMARY = 'user/USER_SUMMARY';
const USER_PROJECTS = 'user/USER_PROJECTS';
const USER_STATISTICS = 'user/USER_STATISTICS';

/* 액션 생성함수 선언 */
export const user_summary = user => ({ 
    type: USER_SUMMARY,
    summary: {
        ...user
    }
});
export const user_projects = user => ({
    type: USER_PROJECTS,
    projects: {
        ...user
    }
});
export const user_statistics = user => ({
    type: USER_STATISTICS,
    statistics: {
        ...user
    }
});

/* 초기 상태 선언 */
const initialState = {
    // id: 85274,
    // login: 'seonkim',
    // name: 'Seongsu Kim',
    // email: 'seonkim@student.42Seoul.kr',
    // correctionPoint: 7,
    // wallet: 25,
    // coalition: 'none',
    // coalitionScore: 0,
    // coalitionRank: 0,
    summary: {},
    projects: {},
    statistics: {}
};

/* 리듀서 선언 */
export default function user(state = initialState, action) {
    switch (action.type) {
        case USER_SUMMARY:
            return action.summary;
        case USER_PROJECTS:
            return action.projects;
        case USER_STATISTICS:
            return action.statistics;
        default:
            return state;
    }
}
