import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import bg from "../../../images/background.jpeg";

const MainStyle = styled.div`
    width: 100%;
    height: 100%;
    background-image: url(${bg});

    @media (min-width: 768px) {
        min-width: 650px;
        max-width: 720px;
    }

    @media (min-width: 576px) {
        max-width: 540px;
    }
`;

const SearchBox = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 500px;
    height: 500px;
    margin: -250px 0px 0px -250px;
    img {
        width: 40%;
        height: 40%;
        display: block;
        margin-left: auto;
        margin-right: auto;
    }

    @media (max-width: 576px) {
        width: 200px;
        height: 200px;
        margin: -100px 0px 0px -100px;
    }
`;

const Input = styled.input`
    width: 300px;

    @media (max-width: 576px) {
        width: 180px;
    }
`;

const Main = () => {
    const [input, setInput] = useState("");
    const [inputError, setInputError] = useState("");

    const history = useHistory();

    const handleChange = (e) => {
        let save = "";

        setInput(e.target.value);
        if (e.target.value.length > 20) {
            save = e.target.value;
            setInputError("최대 20자 이내로 작성해 주세요.");
        }
        if (save !== e.target.value) setInputError("");
    };

    const handleClick = () => {
        if (input && !inputError.length) {
            history.push({ pathname: `/profile/${input}` });
            setInput("");
        }
    };

    return (
        <MainStyle>
            <SearchBox>
                <Link to="/">
                    <img
                        src={
                            require("../../../images/logo_white.png").default
                        }
                    />
                    <Input
                        className={`bg-gray-700 rounded px-2 focus:outline-none text-sm text-white ${
                            inputError.length > 0 ? "mt-4" : ""
                        }`}
                        style={{ height: "35px" }}
                        type="text"
                        placeholder="아이디를 입력해 주세요."
                        autoComplete="off"
                        spellCheck="false"
                        maxLength="25"
                        value={input}
                        onChange={(e) => handleChange(e)}
                        onKeyPress={(e) => {
                            if (e.key === "Enter") handleClick();
                        }}
                    />
                </Link>

            </SearchBox>
        </MainStyle>
    );
};

export default Main;
