import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import { Header, ResHeader, Input, Content, Footer } from "./BaseStyle";

const Base = ({ children }) => {
    const inputStyle = {
        height: "40px",
        alignItems: "center",
        display: "flex",
    };

    const [input, setInput] = useState("");
    const [inputError, setInputError] = useState("");

    const history = useHistory();

    const handleChange = (e) => {
        let save = "";

        setInput(e.target.value);
        if (e.target.value.length > 20) {
            save = e.target.value;
            setInputError("최대 20자 이내로 작성해 주세요.");
        }
        if (save !== e.target.value) setInputError("");
    };

    const handleClick = () => {
        if (input && !inputError.length) {
            history.push({ pathname: `/profile/${input}` });
            setInput("");
        }
    };

    return (
        <>
            <Header>
                <ResHeader>
                    <div style={{ width: "75px", height: "75px" }}>
                        <Link to="/" onClick={() => setInput("")}>
                            <img
                                src={
                                    require("../images/logo.png")
                                        .default
                                }
                            />
                        </Link>
                    </div>
                    <ul style={inputStyle}>
                        <li>
                            <Input
                                className={`bg-gray-700 rounded px-2 focus:outline-none text-sm text-white ${
                                    inputError.length > 0 ? "mt-4" : ""
                                }`}
                                style={{ height: "35px" }}
                                type="text"
                                placeholder="아이디를 입력해 주세요."
                                autoComplete="off"
                                spellCheck="false"
                                maxLength="25"
                                value={input}
                                onChange={(e) => handleChange(e)}
                                onKeyPress={(e) => {
                                    if (e.key === "Enter") handleClick();
                                }}
                            />
                            <p className="text-xs pt-0.5 pl-5 text-red-500">
                                {inputError}
                            </p>
                        </li>
                        <li>
                            <button
                                className="ml-1.5 w-14 rounded text-white bg-blue-400 hover:bg-blue-500 text-sm"
                                style={{ height: "35px" }}
                                onClick={handleClick}
                            >
                                검색
                            </button>
                        </li>
                    </ul>
                </ResHeader>
            </Header>
            <div style={{ paddingTop: "75px", background: "#2e2f32" }}>
                <Content>{children}</Content>
            </div>
            <Footer>
                <div>
                    <a
                        href="https://github.com/nfl1ryxditimo12/42GG/issues"
                        target="_blank"
                        className="text-white"
                    >
                        오류<span className="text-red-600"> & </span>문의
                    </a>
                </div>
            </Footer>
        </>
    );
};

export default Base;
